var AudioEffectTest = cc.Layer.extend({
    _effectId : null,
    init : function(){
        this._super();
        custDiv.innerHTML = "";
        var self = this;

        audioEngine = cc.AudioEngine.getInstance();

        var btnContainer = createBtnContainer(custDiv);

        createBtn(btnContainer, "Play", function(){
            self._effectId = audioEngine.playEffect(res.effect2_mp3, false);
        });
        createBtn(btnContainer, "Pause", function(){
            audioEngine.pauseEffect(self._effectId);
        });
        createBtn(btnContainer, "Stop", function(){
            audioEngine.stopEffect(self._effectId);
        });
        createBtn(btnContainer, "Play in loop", function(){
            self._effectId = audioEngine.playEffect(res.effect2_mp3, true);
        });
        createBtn(btnContainer, "Resume", function(){
            audioEngine.resumeEffect(self._effectId);
        });
        createBtn(btnContainer, "Unload", function(){
            audioEngine.unloadEffect(res.effect2_mp3);
        })
        createBtn(btnContainer, "Pause all", function(){
            audioEngine.pauseAllEffects();
        });
        createBtn(btnContainer, "Stop all", function(){
            audioEngine.stopAllEffects();
        });
        createBtn(btnContainer, "Resume all", function(){
            audioEngine.resumeAllEffects();
        });
        createBtn(btnContainer, "Volume up", function(){
            audioEngine.setEffectsVolume(audioEngine.getEffectsVolume() + 0.1);
            self._logVolume();
        });
        createBtn(btnContainer, "Volume down", function(){
            audioEngine.setEffectsVolume(audioEngine.getEffectsVolume() - 0.1);
            self._logVolume();
        });

        return true;
    },

    _logVolume : function(){
        logTest("Current volume is : " + audioEngine.getEffectsVolume());
    },

    onExit : function(){
        audioEngine.stopAllEffects();
        audioEngine.unloadEffect(res.effect2_mp3);
        custDiv.innerHTML = "";
    }
});
AudioEffectTest.create = function(args){
    var layer = new AudioEffectTest();
    layer.init();
    return layer;
} 