var MusicTest = cc.Layer.extend({
    init : function(){
        this._super();
        custDiv.innerHTML = "";
        var self = this;
        audioEngine = cc.AudioEngine.getInstance();

        this._createCustBtn("Play", function(){
            audioEngine.playMusic(res.background_mp3, false);
        });
        this._createCustBtn("Pause", function(){
            audioEngine.pauseMusic();
        });
        this._createCustBtn("Stop", function(){
            audioEngine.stopMusic();
        });
        this._createCustBtn("Rewind", function(){
            audioEngine.rewindMusic();
        });
        this._createCustBtn("Resume", function(){
            audioEngine.resumeMusic();
        });
        this._createCustBtn("Volume up", function(){
            audioEngine.setMusicVolume(audioEngine.getMusicVolume() + 0.1);
            self._logVolume();
        });
        this._createCustBtn("Volume down", function(){
            audioEngine.setMusicVolume(audioEngine.getMusicVolume() - 0.1);
            self._logVolume();
        });

        return true;
    },

    _logVolume : function(){
        logTest("Current volume is : " + audioEngine.getMusicVolume());
    },

    onExit : function(){
        audioEngine.stopMusic();
        custDiv.innerHTML = "";
    },

    _createCustBtn : function(text, func){
        var playBtn = document.createElement("button");
        playBtn.innerHTML = text;
        custDiv.appendChild(playBtn);
        playBtn.addEventListener("click", func);
    }
});
MusicTest.create = function(args){
    var layer = new MusicTest();
    layer.init();
    return layer;
} 