var MusicTest = cc.Layer.extend({
    init : function(){
        this._super();
        custDiv.innerHTML = "";
        var self = this;
        audioEngine = cc.AudioEngine.getInstance();

        var btnContainer = createBtnContainer(custDiv);

        createBtn(btnContainer, "Play", function(){
            audioEngine.playMusic(res.background_mp3, false);
        });
        createBtn(btnContainer, "Pause", function(){
            audioEngine.pauseMusic();
        });
        createBtn(btnContainer, "Stop", function(){
            audioEngine.stopMusic();
        });
        createBtn(btnContainer, "Rewind", function(){
            audioEngine.rewindMusic();
        });
        createBtn(btnContainer, "Resume", function(){
            audioEngine.resumeMusic();
        });
        createBtn(btnContainer, "Volume up", function(){
            audioEngine.setMusicVolume(audioEngine.getMusicVolume() + 0.1);
            self._logVolume();
        });
        createBtn(btnContainer, "Volume down", function(){
            audioEngine.setMusicVolume(audioEngine.getMusicVolume() - 0.1);
            self._logVolume();
        });

        return true;
    },

    _logVolume : function(){
        logTest("Current volume is : " + audioEngine.getMusicVolume());
    },

    onExit : function(){
        audioEngine.stopMusic();
        custDiv.innerHTML = "";
    }
});
MusicTest.create = function(args){
    var layer = new MusicTest();
    layer.init();
    return layer;
} 