var SchedulerSchedulesAndRemoveTest = cc.Layer.extend({
    ctor:function () {
        this._super();

        this.schedule(this.onTick1, 0.5);
        this.schedule(this.onTick2, 1.0);
        this.schedule(this.onScheduleAndUnschedule, 4.0);
    },

    onTick1:function (dt) {
        logTest("tick1");
    },
    onTick2:function (dt) {
        logTest("tick2");
    },
    onTick3:function (dt) {
        logTest("tick3");
    },
    onTick4:function (dt) {
        logTest("tick4");
    },
    onScheduleAndUnschedule:function (dt) {
        this.unschedule(this.onTick1);
        this.unschedule(this.onTick2);
        this.unschedule(this.onScheduleAndUnschedule);

        this.schedule(this.onTick3, 1.0);
        this.schedule(this.onTick4, 1.0);
    }
});

SchedulerSchedulesAndRemoveTest.create = function(args){
    var layer = new SchedulerSchedulesAndRemoveTest();
    layer.init();
    return layer;
};